import React, { Component } from 'react';
import { Menu, Button, Divider, Form, Grid, Segment } from 'semantic-ui-react';
import GetInstance from '../instance';

class Student extends Component {

    state = {
        account: ''
    };
    async componentDidMount() {
        const { web3, instance } = await new GetInstance().get();

        const accounts = await web3.eth.getAccounts();

        this.setState({ account: accounts[0] });

    };

    render() {

        return (
            <div style={{
                marginLeft: 100,
                marginRight: 100,
                marginTop: 50
            }}>
                <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />

                <Menu style={{marginBottom:50}}>
                    <Menu.Item header>
                        Edu-Ledger
                    </Menu.Item>

                    <Menu.Menu position='right'>
                        <Menu.Item>
                            {this.state.account}
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
                <div>
                    <Segment placeholder>
                        <Grid columns={2} relaxed='very' stackable>
                        <Grid.Column>
                            <Form>
                            <Form.Input
                                iconPosition='left'
                                label='Enter Loan Address'
                                placeholder='Address'
                            />
                            <Button content='Go' primary />
                            </Form>
                        </Grid.Column>

                        <Grid.Column verticalAlign='middle'>
                            <Button content='Create Loan' size='big' />
                        </Grid.Column>
                        </Grid>

                        <Divider vertical>Or</Divider>
                    </Segment>
                </div>
                <div>
                    
                </div>
            </div>
        );
    }
}

export default Student;
