import React, { Component } from 'react';
import GetInstance from '../instance';
import { Menu, Button, Divider, Form, Grid, Segment, Dimmer, Loader } from 'semantic-ui-react';

class StudentDashboard extends Component{

    static getInitialProps({query}) {
        return { query };
    }

    state = {
        loanAddress: '',
        web3: undefined,
        instance: undefined,
        account: ''
    }

    async componentDidMount(){
        this.setState({
            loanAddress: this.props.query.loanAddress
        });
        const { web3, instance } = await new GetInstance().get();

        const accounts = await web3.eth.getAccounts();

        this.setState({
            web3,
            instance,
            account: accounts[0]
        });
    }

    render(){

        return(
            <div style={{
                marginLeft: 100,
                marginRight: 100,
                marginTop: 50
            }}>
                <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
                
                <Menu style={{marginBottom:50}}>
                    <Menu.Item header>
                        Edu-Ledger
                    </Menu.Item>

                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <h4 style={{
                                marginLeft: 20
                            }}>Your Address</h4>{this.state.account}
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
            </div>
        );
    }
}

export default StudentDashboard;
