import React, { Component } from 'react';
import { Menu, Button, Divider, Form, Grid, Segment, Dimmer, Loader } from 'semantic-ui-react';
import GetInstance from '../instance';

const DefaultLoader = (props) => (
    <Segment>
      <Dimmer
          active = {props.active}
          page={true}>
        <Loader />
      </Dimmer>
    </Segment>
  )

class Student extends Component {

    state = {
        account: '',
        loaderActive: false,
        web3: undefined,
        instance: undefined,
        loanAdress: ''
    };
    
    async componentDidMount() {
        const { web3, instance } = await new GetInstance().get();

        const accounts = await web3.eth.getAccounts();

        this.setState({
            web3,
            instance,
            account: accounts[0]
        });

    };

   createLoan = async ()=>{

        try{
            this.setState({ loaderActive: true });
            // await this.state.instance.methods.createEducationLoan().send({
            //     from: this.state.account
            // });
            let myAddress = await this.state.instance.methods.getLoanAddress("souravnihore1998@gmail.com").call();

            if(myAddress === '0x0000000000000000000000000000000000000000'){
                myAddress = `Address not available ...`
            }
            this.setState({
                loaderActive: false,
                loanAdress: myAddress
            });

        }catch(err){
            window.alert(err.message);
        }
    }

    render() {

        return (
            <div style={{
                marginLeft: 100,
                marginRight: 100,
                marginTop: 50
            }}>
                <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
                
                <Menu style={{marginBottom:50}}>
                    <Menu.Item header>
                        Edu-Ledger
                    </Menu.Item>

                    <Menu.Menu position='right'>
                        <Menu.Item>
                            {this.state.account}
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
                <div>
                    <Segment placeholder>
                        <Grid columns={2} relaxed='very' stackable>
                        <Grid.Column>
                            <Form>
                            <Form.Input
                                iconPosition='left'
                                label='Enter Loan Address'
                                placeholder='Address'
                            />
                            <Button content='Go' primary />
                            </Form>
                        </Grid.Column>

                        <Grid.Column verticalAlign='middle'>
                        <Form>
                            <Form.Input
                                iconPosition='left'
                                label='Enter Email'
                                placeholder='Email'
                            />
                            <Button content='Create Loan' size='big' onClick={this.createLoan}/>
                            </Form>  
                        </Grid.Column>
                        </Grid>

                        <Divider vertical>Or</Divider>
                    </Segment>
                </div>
                <div style={{
                    marginTop: 50,
                    color: 'grey'
                }}>
                    <h4>Your Loan Address (save this in somewhere):</h4>
                    <h1 style={{color:'green'}}>{this.state.loanAdress}</h1>
                    <DefaultLoader active={this.state.loaderActive} />
                </div>
            </div>
        );
    }
}

export default Student;
