import React, { Component } from 'react';
import GetInstance from '../instance';
import { Menu, Card, Button, Divider, Form, Grid, Segment, Dimmer, Loader } from 'semantic-ui-react';

class StudentDashboard extends Component{

    static getInitialProps({query}) {
        return { query };
    }

    state = {
        account: '',
        loanAddress: '',
        web3: undefined,
        instance: undefined
    }

    async componentDidMount(){
        this.setState({
            loanAddress: this.props.query.loanAddress
        });
        const { web3, instance } = await new GetInstance().get();

        const accounts = await web3.eth.getAccounts();

        this.setState({
            web3,
            instance,
            account: accounts[0]
        });
    }

    render(){

        return(
            <div style={{
                marginLeft: 100,
                marginRight: 100,
                marginTop: 50
            }}>
                <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
                
                <div>
                <Menu style={{marginBottom:50}}>
                    <Menu.Item header>
                        Edu-Ledger
                    </Menu.Item>

                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <h4 style={{
                                marginRight: 10,
                                color: 'green'
                            }}>Your Address:</h4>{this.state.account}
                        </Menu.Item>
                        <Menu.Item>
                            <h4 style={{
                                marginRight: 10,
                                color: 'green'
                            }}>Loan Address:</h4>{this.state.loanAddress}
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
                </div>
                <div>
                <Card.Group>
                    <Card>
                        <Card.Content header='About Amy' />
                        <Card.Content>
                            <Form>
                                <Form.Input
                                    label='College Address'
                                    placeholder='Address'
                                />
                                <Form.Input
                                    label='Confirmed Admission Receipt'
                                    placeholder='Link'
                                />
                                <Button content='Upload' primary />
                            </Form>
                        </Card.Content>
                    </Card>
                    <Card>
                        <Card.Content header='About Amy' />
                        <Card.Content>
                            <Form>
                                <Form.Input
                                    label='Select Bank'
                                    placeholder='Index'
                                />
                                <Button content='Select' primary />
                            </Form>
                        </Card.Content>
                    </Card>
                </Card.Group>
                </div>
            </div>
        );
    }
}

export default StudentDashboard;
